mapper.select:
SELECT bin_id
FROM bin
WHERE zone = #{zone}
    AND aisle = #{aisle}
ORDER BY row ASC, floor ASC, amount ASC
LIMIT 1

SELECT bin_id
FROM bin
WHERE zone = #{zone}
    AND aisle = #{aisle}
    AND row_num = #{rowNum}
ORDER BY floor ASC, amount ASC
LIMIT 1

SELECT bin_id
FROM bin
WHERE bin_code = #{binCode}

SELECT bin_id
FROM bin
WHERE bin_code = #{binCode}

mapper.update:
UPDATE bin
SET amount = amount + #{lotCount}
WHERE bin_id = #{binId}
