mapper.select:
SELECT p.product_id, p.product_code, p.product_name, i.available_quantity AS productCount FROM product p JOIN inventory i ON p.product_id = i.product_id
WHERE p.product_code LIKE CONCAT('%', #{productCode}, '%')
${order.property}
ASC DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT p.product_id, p.product_code, p.product_name, i.available_quantity AS productCount, p.threshold AS productThreshold FROM product p JOIN inventory i ON p.product_id = i.product_id
WHERE p.product_code LIKE CONCAT('%', #{productCode}, '%')
${order.property}
ASC DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}
