mapper.select:
SELECT o.order_id, o.order_number, o.order_status, s.supplier_name, o.supplier_id, o.order_date, o.is_approved, o.is_return_order, o.order_date AS delivery_deadline, op.product_id, op.product_name, op.product_count FROM ( SELECT o.* FROM `order` o WHERE 1=1 ) o JOIN supplier s ON o.supplier_id = s.supplier_id LEFT JOIN order_product op ON o.order_id = op.order_id WHERE 1=1
AND o.order_number LIKE CONCAT('%', #{orderNumber}, '%')
AND o.order_date >= #{startDate}
AND o.order_date <= #{endDate}
ORDER BY
${pageable.sort.toString().replace('ORDER BY', '')},
o.order_date DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT COUNT(*) FROM `order`
order_number IS NOT NULL
AND order_date >= #{startDate}
AND order_date <= #{endDate}
AND order_date LIKE CONCAT('%', #{outboundOrderNumber}, '%')
